'use strict';

function achvutil () {
}

achvutil.prototype.getPrevdate = function (days)
{
  var date = new Date();
  date.setDate(date.getDate() - days);
  var dd = date.getDate();
  var mm = date.getMonth()+1; //January is 0!

  var yyyy = date.getFullYear();
  if(dd<10){
        dd='0'+dd;
  } 
  if(mm<10){
        mm='0'+mm;
  } 
  return yyyy + "." + mm + "." + dd;
}

module.exports = function () {return new achvutil()}
